settings.custom_liquid:
{% comment %} Bundle Thickness Selector {% endcomment %}
{% assign bundle_4mm_handle = 'diy-feeltrue-sandal-kit' %}
{% assign bundle_6mm_handle = 'diy-feeltrue-sandal-kit-bundle-6mm' %}

{% if product.handle == bundle_4mm_handle or product.handle == bundle_6mm_handle %}

  <div class="bundle-thickness-selector">
    <p class="selector-label">Choose Sole Thickness:</p>

    <div class="thickness-options">
      <a href="/products/{{ bundle_4mm_handle }}" 
         class="thickness-option {% if product.handle == bundle_4mm_handle %}active{% endif %}">
        <span class="thickness-size">4mm</span>
        <span class="thickness-label">Connect</span>
      </a>

      <a href="/products/{{ bundle_6mm_handle }}" 
         class="thickness-option {% if product.handle == bundle_6mm_handle %}active{% endif %}">
        <span class="thickness-size">6mm</span>
        <span class="thickness-label">Connect</span>
      </a>
    </div>

    <p class="thickness-description">
      {% if product.handle == bundle_4mm_handle %}
        Minimal ground feel, maximum flexibility
      {% else %}
        Extra comfort and protection
      {% endif %}
    </p>
  </div>

  <style>
    .bundle-thickness-selector {
      padding: 15px 0;
      margin: 20px 0;
      border-top: 1px solid #e0e0e0;
      border-bottom: 1px solid #e0e0e0;
    }

    .selector-label {
      font-weight: 600;
      margin-bottom: 12px;
      color: #333;
      font-size: 16px;
    }

    .thickness-options {
      display: flex;
      gap: 12px;
      margin-bottom: 10px;
    }

    .thickness-option {
      flex: 1;
      padding: 12px 20px;
      background: #ffffff;
      border: 2px solid #e0e0e0;
      border-radius: 8px;
      text-decoration: none;
      color: #333;
      text-align: center;
      transition: all 0.3s ease;
      display: flex;
      flex-direction: column;
      align-items: center;
      gap: 4px;
      cursor: pointer;
    }

    .thickness-option:hover {
      border-color: #333;
      background: #f8f8f8;
      transform: translateY(-2px);
      box-shadow: 0 4px 8px rgba(0,0,0,0.1);
    }

    .thickness-option.active {
      background: #333;
      color: white;
      border-color: #333;
      font-weight: 600;
      cursor: default;
      pointer-events: none;
    }

    .thickness-option.active:hover {
      transform: none;
      box-shadow: none;
    }

    .thickness-size {
      font-size: 20px;
      font-weight: bold;
      display: block;
    }

    .thickness-label {
      font-size: 13px;
      opacity: 0.9;
    }

    .thickness-description {
      margin-top: 10px;
      font-size: 14px;
      color: #666;
      font-style: italic;
      text-align: center;
    }

    @media (max-width: 480px) {
      .thickness-options {
        flex-direction: column;
      }

      .thickness-option {
        width: 100%;
      }
    }
  </style>

{% endif %}
